SELECT * FROM ( SELECT p.*, rownum rNum FROM ( SELECT DISTINCT place.*, pr.main_cate, CASE WHEN i.user_id IS NOT NULL THEN 'liked' ELSE 'notLiked' END AS likeStatus FROM place LEFT JOIN place_preference pp ON place.place_id = pp.place_id LEFT JOIN preference pr ON pp.preference_id = pr.preference_id LEFT JOIN interests i ON place.place_id = i.place_id AND i.user_id = #{userId}
place_name LIKE '%' || #{str} || '%'
AND
location LIKE '%' || #{str} || '%'
AND
main_cate LIKE #{str}
ORDER BY place.place_id DESC ) p ) WHERE rNum BETWEEN #{start} AND #{end}

SELECT * FROM ( SELECT p.*, rownum rNum FROM ( SELECT DISTINCT place.*, pr.main_cate, CASE WHEN i.user_id IS NOT NULL THEN 'liked' ELSE 'notLiked' END AS likeStatus FROM place LEFT JOIN place_preference pp ON place.place_id = pp.place_id LEFT JOIN preference pr ON pp.preference_id = pr.preference_id LEFT JOIN interests i ON place.place_id = i.place_id AND i.user_id = #{userId}
place_name LIKE '%' || #{str} || '%'
AND
location LIKE '%' || #{str} || '%'
AND
main_cate LIKE #{str}
ORDER BY place.likes DESC ) p ) WHERE rNum BETWEEN #{start} AND #{end}

SELECT * FROM interests i
INNER JOIN place p ON i.place_id = p.place_id
WHERE user_id = #{userId}
